/*!40101 SET NAMES utf8mb4 */;
CREATE TABLE `arac` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `plaka` varchar(100) NOT NULL,
  `kategori` varchar(20) NOT NULL,
  `marka` varchar(100) NOT NULL,
  `zimmet` varchar(250) NOT NULL,
  `yolcusayisi` varchar(10) NOT NULL,
  `muayene` datetime DEFAULT NULL,
  `muayanedurum` int(11) DEFAULT NULL,
  `sigorta` datetime DEFAULT NULL,
  `egzoz` datetime DEFAULT NULL,
  `gizle` int(10) DEFAULT 0 COMMENT 'Aracın görev eklerken listede görünmesi için 0 değeri atanmalıdır.',
  `takip` int(10) DEFAULT 0 COMMENT 'Cron görevlerinde yer alması için 0 değeri atanmalıdır.',
  `arsiv` int(10) DEFAULT 0 COMMENT 'Aktif kullanılan araçlar için 0 değeri atanmalıdır.',
  PRIMARY KEY (`id`),
  KEY `id` (`id`,`plaka`),
  KEY `yolcusayisi` (`yolcusayisi`)
) ENGINE=InnoDB AUTO_INCREMENT=104 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `gorev` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `soforid` int(11) DEFAULT 6,
  `yurtid` int(11) DEFAULT 16,
  `varisyeri` varchar(250) DEFAULT 'Düzenlenecek',
  `aracid` int(11) DEFAULT NULL,
  `bstarih` datetime NOT NULL,
  `bttarih` datetime DEFAULT NULL,
  `yetkili` varchar(250) NOT NULL,
  `ilolur` varchar(50) DEFAULT NULL,
  `aciklama` text DEFAULT NULL COMMENT 'Görev Açıklaması',
  `gizle` int(10) DEFAULT 0 COMMENT '1 Değeri gizler.',
  `durum` int(11) DEFAULT NULL,
  `aktarildi` int(2) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `durum` (`durum`),
  KEY `soforid` (`soforid`,`yurtid`,`aracid`,`bstarih`),
  KEY `varisyeri` (`varisyeri`),
  KEY `bstarih` (`bstarih`),
  KEY `bttarih` (`bttarih`)
) ENGINE=InnoDB AUTO_INCREMENT=2172 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `gorevlendirmeler` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `soforid` int(11) NOT NULL,
  `bstarih` datetime NOT NULL,
  `bttarih` datetime NOT NULL,
  `aracid` int(22) NOT NULL,
  `gorev` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=170 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci COMMENT='Görevlendirmelerin bulunduğu tablo';
CREATE TABLE `izin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `soforid` int(11) NOT NULL,
  `bstarih` date DEFAULT NULL,
  `bttarih` date DEFAULT NULL,
  `izin` int(11) NOT NULL,
  `aciklama` text DEFAULT NULL,
  `gun` int(11) NOT NULL,
  `saat` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `soforid` (`soforid`)
) ENGINE=InnoDB AUTO_INCREMENT=262 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `log` (
  `id` bigint(255) NOT NULL AUTO_INCREMENT,
  `soforid` int(11) NOT NULL,
  `islem` varchar(500) NOT NULL,
  `tarih` datetime NOT NULL,
  `ip` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6914 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `malzeme` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `soforid` int(11) NOT NULL,
  `yurtid` int(11) NOT NULL,
  `varisyeri` text NOT NULL,
  `bstarih` datetime NOT NULL,
  `aciklama` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `mesai` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `soforid` int(11) NOT NULL,
  `bstarih` datetime NOT NULL,
  `bttarih` datetime NOT NULL,
  `mesai` int(11) NOT NULL,
  `aracid` int(11) NOT NULL,
  `gorev` text NOT NULL,
  `pazargunu` int(11) NOT NULL,
  `durum` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `soforid` (`soforid`)
) ENGINE=InnoDB AUTO_INCREMENT=800 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `sofor` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `adsoyad` varchar(255) NOT NULL,
  `kullaniciadi` varchar(200) NOT NULL,
  `sifre` varchar(250) NOT NULL DEFAULT '50c717b51725e28e23bc0ff093d57a82',
  `email` varchar(255) NOT NULL,
  `yonetici` int(10) NOT NULL,
  `gg` int(11) DEFAULT NULL COMMENT '0 göster  1 gizle',
  `girisizni` int(11) DEFAULT NULL COMMENT '0 giriş yapabilir 1 sisteme girişini yasaklar',
  PRIMARY KEY (`id`),
  KEY `id` (`id`,`adsoyad`),
  KEY `id_2` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=105 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
CREATE TABLE `yurt` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT,
  `ad` varchar(255) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `ad` (`ad`)
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;
